mapper.select:
SELECT student.name AS studentNo, student.no AS studentName, SUM(CASE WHEN course_attendance.status = 0 THEN 1 ELSE 0 END) AS nocheckCount, SUM(CASE WHEN course_attendance.status = 1 THEN 1 ELSE 0 END) AS signedCount, SUM(CASE WHEN course_attendance.status = 2 THEN 1 ELSE 0 END) AS absentCount, SUM(CASE WHEN course_attendance.status = 3 THEN 1 ELSE 0 END) AS leaveCount FROM student JOIN course_attendance ON student.id = course_attendance.student_id WHERE course_attendance.course_id IN (
#{courseDetailItem.id}
) # 使得学生不重复 GROUP BY student_id;

SELECT student.no AS studentNo,
       student.name AS studentName,
       course_detail.week AS week,
       course_detail.weekday AS weekday,
       course_attendance.status AS status
FROM course_attendance
         JOIN student ON course_attendance.student_id = student.id
         JOIN course_detail ON course_detail.id = course_attendance.course_id
WHERE course_detail.course_name = #{courseName}
  AND student.no = #{studentNo}
  AND course_detail.semester = #{semester}
  AND course_detail.course_teacher = #{teaUserid}
ORDER BY course_detail.begin_time;
